import React from 'react';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const MyTitle: React.FC = () => {
  const [index, setIndex] = React.useState(0);
  const handleChangeIndex = (idx: number) => {
    setIndex(idx);
  }
  const styles = {
    slide: {
      color: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
      display: 'flex',
      fontFamily: 'Julius Sans One',
      width: '200px',
    },
  };
  return (
    <AutoPlaySwipeableViews index={index} enableMouseEvents onChangeIndex={handleChangeIndex} axis="x" springConfig={{duration: '3s', easeFunction: 'ease-in-out', delay: '0s'}}>
      <div style={Object.assign({}, styles.slide)}>
        A business systems analyst
      </div>
      <div style={Object.assign({}, styles.slide)}>
        An UI/UX designer
      </div>
      <div style={Object.assign({}, styles.slide)}>
        A creative web developer with a focus on frontend
      </div>
    </AutoPlaySwipeableViews>
  );
}

export default MyTitle;
