import React from 'react';

interface TitlePaginationProps {
  index: number;
  //number of dots/ indicatots/ slides 
  totalDots: number;
}
const TitlePagination: React.FC<TitlePaginationProps> = (props) => {
  const {index, totalDots} = props;
  const styles = {
    dot: {
      cursor: 'pointer',
      height: '15px',
      width: '15px',
      margin: '6vh 2vh 0 2vh',
      backgroundColor: '#bbb',
      borderRadius: '50%',
      display: 'inline-block',
      transition: 'backgroundColor 0.6s ease',
    },
    activeDot: {
      cursor: 'pointer',
      height: '15px',
      width: '15px',
      margin: '6vh 2vh 0 2vh',
      backgroundColor: '#fff',
      borderRadius: '50%',
      display: 'inline-block',
      transition: 'backgroundColor 0.6s ease',
    },
  };
  return (
    <div style={{ flexDirection: 'row' }}>
      {
        Array(totalDots).fill(0).map((_, i) => (
          <div style={i === index ? styles.dot : styles.activeDot}/>
        ))
      }
    </div>
  );
};

export default TitlePagination;
