import React from 'react';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import TitlePagination from './MyTitleContainer/TitlePagination';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const myTitles = [
  "A business systems analyst",
  "An UI/UX designer",
  "A creative web developer with a focus on frontend",
];

const MyTitle: React.FC = () => {
  const [index, setIndex] = React.useState(2);
  React.useEffect(() => {
    const handleChangeIndex = (idx: number) => {
      setIndex(idx);
    }
  })
  const handleChangeIndex = (idx: number) => {
    setIndex(idx);
  }
  const styles = {
    slide: {
      color: '#fff',
      alignItems: 'right',
      justifyContent: 'center',
      display: 'flex',
      fontFamily: 'Julius Sans One',
      fontSize: '20vw',
    },
  };
  return (
    <React.Fragment>
      {
        myTitles.map(
          (title, i) => (
            (i === index) && <span style={styles.slide}>{title}</span>
          )
        )
      }
      {/* <div style={{ flexDirection: 'row' }}>
        {
          Array(myTitles.length).fill(0).map((_, i) => (
            <TitlePagination selected={i === index} onClick={(i) => handleChangeIndex}/>
          ))
        }        
      </div> */}
    </React.Fragment>
  );
}

export default MyTitle;
